sys_variable_value.value:
(function execute(inputs, outputs) {

  for (i = 1; i <= 9; i++) {
    var gr = new GlideRecord('x_134851_benford_data');
    gr.addEncodedQuery('data_set='+inputs.dataset_id+'^data_value_stringSTARTSWITH'+i);
    gr.query();
    while (gr.next()) {
      var detail = new GlideRecord('x_134851_benford_analysis_detail');
      detail.addEncodedQuery('leading_digit='+i+'^analysis.data_set='+inputs.dataset_id);
      detail.query();
      if (detail.next()) {
        detail.count = detail.count+1;
        detail.update();
      }
    }
  }



})(inputs, outputs);

