sys_variable_value.value:
(function execute(inputs, outputs) {

  var total=0;

  var gr = new GlideRecord('x_134851_benford_analysis_detail');
  gr.addEncodedQuery('analysis.data_set='+inputs.dataset_id);
  gr.query();
  while (gr.next()) {
    total = total+gr.count;
  }

  for (i=1; i<=9; i++) {
    var detail = new GlideRecord('x_134851_benford_analysis_detail');
    detail.addEncodedQuery('analysis.data_set='+inputs.dataset_id+'^leading_digit='+i);
    detail.query();
    if (detail.next()) {
      detail.percentage = detail.count*100/total;
      detail.update();
    }
  }

})(inputs, outputs);

